intrinsic.oper:
IF (q + size - 1) &lt; q.LB OR (q + size - 1) &gt; q.UB THEN
	#BR;
FI;

IF q &lt; q.LB THEN
	#BR;
FI;

IF q &gt; q.UB THEN
	#BR;
FI;

dst := q;
dst.LB := r.LB;
dst.UB := r.UB;

dst := q;
dst.LB := 0;
dst.UB := 0;

dst := q;
IF r.LB &gt; (q + size - 1) OR r.UB &lt; q THEN
	dst.LB := 1;
	dst.UB := 0;
ELSE
	dst.LB := MAX(r.LB, q);
	dst.UB := MIN(r.UB, (q + size - 1));
FI;

dst := srcmem;
dst.LB := srcmem.LB;
dst.UB := srcmem + size - 1;

X3[31:0] := a[127:96]
X2[31:0] := a[95:64]
X1[31:0] := a[63:32]
X0[31:0] := a[31:0]
RCON[31:0] := ZeroExtend(imm8[7:0]);
dst[31:0] := SubWord(X1)
dst[63:32] := (RotWord(SubWord(X1)) XOR RCON;
dst[95:64] := SubWord(X3)
dst[127:96] := RotWord(SubWord(X3)) XOR RCON;

IF (imm8[0] = 0)
	TEMP1 := a[63:0];
ELSE
	TEMP1 := a[127:64];
FI 
IF (imm8[4] = 0)
	TEMP2 := b[63:0];
ELSE 
	TEMP2 := b[127:64];
FI

FOR i := 0 to 63
	TEMP[i] := (TEMP1[0] and TEMP2[i]);
	FOR j := 1 to i
		TEMP [i] := TEMP [i] XOR (TEMP1[j] AND TEMP2[i-j])
	ENDFOR 
	dst[i] := TEMP[i];
ENDFOR
FOR i := 64 to 127
	TEMP [i] := 0;
	FOR j := (i - 63) to 63
		TEMP [i] := TEMP [i] XOR (TEMP1[j] AND TEMP2[i-j])
	ENDFOR
	dst[i] := TEMP[i];
ENDFOR
dst[127] := 0

