// override comparisons for invalid characters CASE (imm8[3:2]) OF 0: // equal any IF (!aInvalid && bInvalid) BoolRes[i][j] := 0 ELSE IF (aInvalid && !bInvalid) BoolRes[i][j] := 0 ELSE If (aInvalid && bInvalid) BoolRes[i][j] := 0 FI 1: // ranges IF (!aInvalid && bInvalid) BoolRes[i][j] := 0 ELSE IF (aInvalid && !bInvalid) BoolRes[i][j] := 0 ELSE If (aInvalid && bInvalid) BoolRes[i][j] := 0 FI 2: // equal each IF (!aInvalid && bInvalid) BoolRes[i][j] := 0 ELSE IF (aInvalid && !bInvalid) BoolRes[i][j] := 0 ELSE If (aInvalid && bInvalid) BoolRes[i][j] := 1 FI 3: // equal ordered IF (!aInvalid && bInvalid) BoolRes[i][j] := 0 ELSE IF (aInvalid && !bInvalid) BoolRes[i][j] := 1 ELSE If (aInvalid && bInvalid) BoolRes[i][j] := 1 FI ESAC ENDFOR ENDFOR
// aggregate results CASE (imm8[3:2]) OF 0: // equal any IntRes1 := 0 FOR i := 0 to UpperBound FOR j := 0 to UpperBound IntRes1[i] := IntRes1[i] OR BoolRes[i][j] ENDFOR ENDFOR 1: // ranges IntRes1 := 0 FOR i := 0 to UpperBound FOR j := 0 to UpperBound, j += 2 IntRes1[i] := IntRes1[i] OR (BoolRes[i][j] AND BoolRes[i][j+1]) ENDFOR ENDFOR 2: // equal each IntRes1 := 0 FOR i := 0 to UpperBound IntRes1[i] := BoolRes[i][i] ENDFOR 3: // equal ordered IntRes1 := (imm8[0] ? 0xFF : 0xFFFF) FOR i := 0 to UpperBound k := i FOR j := 0 to UpperBound-i IntRes1[i] := IntRes1[i] AND BoolRes[k][j] k++ ENDFOR ENDFOR ESAC
// optionally negate results bInvalid := 0 FOR i := 0 to UpperBound IF imm8[4] IF imm8[5] // only negate valid IF b[n+size-1:n] == 0 bInvalid := 1 FI IF bInvalid // invalid, don't negate IntRes2[i] := IntRes1[i] ELSE // valid, negate IntRes2[i] := -1 XOR IntRes1[i] FI ELSE // negate all IntRes2[i] := -1 XOR IntRes1[i] FI ELSE // don't negate IntRes2[i] := IntRes1[i] FI ENDFOR
